;Wild Commander;(C) BUDDER/MGN/2014;---------------------------------------        ORG #6011;-------AFKTIME EQU 50*60*1DEFRQ   EQU %00000001;AY+Z80CACHE   EQU %00000010INTM    EQU #5BFF;4 bytes!;-------BASIC48 EQU %00000011;-------STS     EQU #27AFBORDER  EQU #0FAFVmod    EQU #00AFVpag    EQU #01AFVpal    EQU #07AFVfm     EQU #15AFVtm     EQU #16AFVtl0    EQU #17AFVtl1    EQU #18AFVsgp    EQU #19AFVsinL   EQU #23AFVsinH   EQU #24AFPW0     EQU #10AFPW1     EQU #11AFPW2     EQU #12AFPW4     EQU #13AFSYC     EQU #20AFCYC     EQU #2BAFPE0     EQU #21AFYoffL   EQU #04AFYoffH   EQU #05AFXoffL   EQU #02AFXoffH   EQU #03AF;-------DMASL   EQU #1AAFDMASH   EQU #1BAFDMASX   EQU #1CAFDMADL   EQU #1DAFDMADH   EQU #1EAFDMADX   EQU #1FAFDMA_T   EQU #28AF;0=1, 255=256DMA_N   EQU #26AF;255=512DMA_C   EQU #27AF;RW 1 - - - 0 0 1;-------RSQB    EQU #8000ELN     EQU 32;64CURclr  EQU %01011000;\CUR COLOR in PANzCURmsk  EQU %00000111;/MASKTBG     EQU %00010111;TEXT COLORMARKclr EQU %00010011WNDCLR  EQU %00011111GSTP    EQU 4;Step (GFX Viewer)PLHPG   EQU 127;     HEADERSPLGPG   EQU PLHPG+1; PLUGINSPLEPG   EQU PLGPG+32;/TVBPG   EQU 26;      TXTbuf Start (Page)TVEPG   EQU TVBPG+63;TXTbuf End (1024Kb)TVvPG   EQU 8;   TXT/HEX video pageRESB    EQU 10GVvPG   EQU 32;      GFX video page1GV2PG   EQU GVvPG+16;GFX video page2GV3PG   EQU GV2PG+16;GFX video page3GV4PG   EQU GV3PG+16;GFX video page4CTZPG   EQU 14;5 pages per CATPNLPG   EQU #F0;DriversINIPG   EQU PNLPG+2STRPG   EQU PNLPG+3;StreamsCPPG1   EQU 24;\CPPG2   EQU 25;/ positionsD2PG    EQU #F6D5PG    EQU #F5DBPG    EQU #F4RPPG    EQU #F7;-------RLPNL   EQU #0000RRPNL   EQU #1000WINDC   EQU #5B02;(32)TXTBU   EQU #5B22;(64)PG0     EQU #6000PG4     EQU PG0+1PG8     EQU PG0+2PGC     EQU PG0+3ABT     EQU PG0+4ENT     EQU PG0+5TMN     EQU PG0+9FEP     EQU PG0+11;\XPP     EQU PG0+12;/CNFv    EQU PG0+13HEI     EQU PG0+14;\TXTmdCO EQU PG0+15;/INIflg  EQU PG0+16RSQBC   EQU PG0+17RSQB2   EQU RSQBC+2Fun     EQU #6006LOBU    EQU #B000;Main BufferDNT     EQU #C000;CATZCURO    EQU #C000;POZs in browsingENCODET EQU #D000;(TVvPG+1);---------------------------------------        DI        EX (SP),HL:LD SP,#6000-2        EX (SP),HL        CALL INIT,#C000        DIINTA    LD HL,I_N_T,(INTM+0),HL        LD HL,#C9FB,(INTM+2),HL        DI        LD A,INTM[,I,A:IM 2        CALL FINI,DEF        CALL PATH;-------MAIN    EI:HALT:CALL CURSOR        CALL ALT,NZ,F12:JR NZ,MAIN        CALL UPPP,NZ,CRUP        CALL DWWW,NZ,CRDN        CALL PGU,NZ,PGSUP        CALL PGD,NZ,PGSDN        CALL HOME,NZ,PGSHM        CALL END,NZ,PGSED        CALL LFFF,NZ,PGSHM        CALL RGGG,NZ,PGSED        CALL BSPC,NZ,BENT        CALL STATUS        CALL SHIFT,NZ,R2B2:JR NZ,MAIN        CALL F1,NZ,HELP        CALL F3,NZ,VIEW        CALL F10,NZ,F11MENU        CALL TABK,NZ,XPAN        CALL F5,NZ,COPY        CALL F6,NZ,Move        CALL F7,NZ,MkDir        CALL F8,NZ,DelFl        CALL F9,NZ,F9STP        CALL DELE,NZ,DelFl        CALL INS,NZ,Mark        CALL SPKE,NZ,Mark        CALL TMAFK        CALL ENKE,NZ,ENTR        CALL ESC:JP Z,MAIN        CALL GED        CALL MNG0D        LD BC,Vmod:XOR A:OUT A        RET;---------------------------------------Mark    CALL GETEN:JP Z,CRDN        DEC DE        LD A,(DE)        OR A:LD A,MARKclr:JR Z,$+3:XOR A        LD (DE),A        LD L,(IX+16)        LD H,(IX+17)        PUSH HL        CALL CRDN        POP HL        LD E,(IX+16)        LD D,(IX+17):OR A:SBC HL,DE        RET NZ        CALL CURSER,WLIT,CURSOR        RETIMrk    CALL GETEN:RET Z        DEC DE        LD A,(DE)        OR A:LD A,MARKclr:JR Z,$+3:XOR A        LD (DE),A        CALL WLIT        LD A,1,(MRCT),A:OR A        RET;---------------------------------------STATUS  LD A,(IX+10):CP 2:RET NZ        XOR A:LD HL,STUS:CP (HL):RET Z        LD (HL),A        CALL GETEN        EXA        LD HL,ENTRY,DE,LOBU,BC,8:LDIR        LD A,32,(DE),A:INC DE        LD C,3:LDIR        LD (DE),A:INC DE        EXA        LD HL,SDIR:JR Z,$+5:LD HL,SFIL        LD C,5:LDIR        LD A,32.7      LD (DE),A:INC DE        EXX        LD HL,(ARRR),DE,(ARRR+2)        PUSH IX        CALL DECZON4        POP IX        DEC DE        LD HL,DE,BC,0-9:ADD HL,BC        LD B,10,A,#30DZF     CP (HL):JR NZ,DzF        LD (HL),32:INC HL        DJNZ DZFDzF     LD HL,DE.3      INC DE        LD A,32        LD BC,3:LDDR        LD (DE),A:DEC DE        LD C,3:LDDR        LD (DE),A:DEC DE        LD C,3:LDDR        LD (DE),A:DEC DE        LD A,(HL),(DE),A        LD A,(IX+5):SUB 2_LD HL,LOBU,D,A,E,1,BC,38:CALL PRSRW        RET;-------R2B2    CALL ENKE:JP NZ,EXTZEL        LD A,1:OR A        RET;-------F12     CALL F1:JR NZ,SLM        CALL F2:JR NZ,SRM        JR BER;-------SLM     LD HL,WINDOW:JR $+5SRM     LD HL,WINDO2        CALL SXM:INC B:JR Z,BER        PUSH IX        LD IX,(IXHL),(IX+29),A,(IX+28),0        CALL DRACT,ORIGA        POP IX        CALL DRACTBER     LD A,1:OR A        RET;-------SXM     ;i:HL - SOW, IX - SOW(now);        o: A - CurPoz-1        PUSH IX        PUSH HL:CALL CURSER:POP HL        LD (IXHL),HL        LD DE,WINDC,BC,32:LDIR        LD IX,WINDC        LD (IX+1),0.2      INC (IX+2).2      INC (IX+3)        LD (IX+4),32        LD (IX+5),3+2        LD (IX+6),%01111111        LD (IX+8),B        LD (IX+9),B        LD (IX+10),B        LD (IX+11),B        LD A,(IX+29):INC A        LD (IX+12),A;CUR        LD (IX+13),3;CURMAX        LD (IX+14),%11100000;CLR        CALL PRWOW_LD HL,TXT4,DE,#0009,BC,14:CALL PRSRW_LD A,%11110111:CALL PRIAT_LD HL,THDD,DE,#0102,BC,16:CALL PRSRW_LD HL,TSDZ,DE,#0208,BC,16:CALL PRSRW_LD HL,TSDG,DE,#0308,BC,16:CALL PRSRWSMC     EI:HALT:CALL CURSOR        CALL UPPP,NZ,CrUP        CALL DWWW,NZ,CrDN        CALL ESC:LD B,#FF:JR NZ,AMC        CALL ENKE:JR Z,SMC        LD B,0AMC     PUSH BC        CALL RRESB        POP BC        LD A,(IX+12):DEC A        POP IX        RET;---------------------------------------ENTR    CALL GETEN:JP NZ,VIE        INC DE:LD A,(DE):OR A:RET Z        CALL MPATH        CALL CURSER        JP FOLDERBENT    LD HL,0        CALL GETen:RET NZ        INC DE:LD A,(DE):CP ".":RET NZ        CALL MPATH        CALL CURSER        XOR A        LD (IX+12),1        LD (IX+16),A        LD (IX+17),A        JP FOLDER;---------------------------------------XPAN    PUSH IX        POP HL        LD DE,WINDO2:OR A:SBC HL,DE        JR Z,LPAN;-------RPAN    CALL CURSER        LD IX,WINDO2:CALL DRACT        JP STTLPAN    CALL CURSER        LD IX,WINDOW:CALL DRACT        JP STT;---------------------------------------PGSED   CALL CURSER        LD A,(IX+20):OR A:JR NZ,CR4        LD L,(IX+16),H,(IX+17)EDD     PUSH HL        CALL Gwe        POP HL:INC HL:JR NZ,EDD        LD A,(IX+5):SUB 1,(IX+10)        LD B,0,C,A        OR A:SBC HL,BC        LD A,H:CP #FF:RET NC        LD (IX+16),L,(IX+17),H        CALL WLIT        JR CR4PGSDN   CALL CURSER        LD A,(IX+20):OR A:JR NZ,CR4        LD A,(IX+5):SUB 2,(IX+10)        LD C,A,B,0        LD L,(IX+16),H,(IX+17):ADD HL,BC        LD (IX+16),L,(IX+17),H        CALL WLITCR4     CALL CR4EG        JP STTSM      PUSH HL:CALL CURSER:POP HL        JR SLPGSUP   ;LD A,(IX+21):OR A:RET NZ        LD A,(IX+5):SUB 2,(IX+10)        LD C,A,B,0        LD L,(IX+16),H,(IX+17)        OR A:SBC HL,BC:JR NC,SMPGSHM   CALL CURSER        LD HL,0,(IX+12),1        JR SL;-------CRDN    LD A,(IX+12)        CP (IX+13):JR NC,SRLDNRDN     CALL CURSER        INC (IX+12)        JR STTSRLDN   LD A,(IX+20):OR A:RET NZ        LD L,(IX+16),H,(IX+17):INC HLSL      LD (IX+16),L,(IX+17),H        CALL WLIT        CALL CURSOR        RETCR4EG   LD A,(IX+20):OR A:RET Z        LD A,(IX+13):OR A:RET Z        LD (IX+12),A        RETCR4eg   LD A,(IX+13)        OR A:JR NZ,$+3:INC A        CP (IX+12):RET NC        LD (IX+12),A        RET;-------CRUP    LD A,(IX+12)        CP 1:JR Z,SRLUPRUP     CALL CURSER        DEC (IX+12)STT     CALL CURSOR        LD A,1,(STUS),A        RETSRLUP   LD A,(IX+21):OR A:RET NZ        LD L,(IX+16),H,(IX+17):DEC HL        JR SL;-------CrDN    LD A,(IX+12)        CP (IX+13):RET NC        JR RDNCrUP    LD A,(IX+12)        CP 1:RET Z        JR RUP;-------CrPU    CALL CURSER:LD (IX+12),1:JR STTCrPD    CALL CURSER        LD A,(IX+13),(IX+12),A:JR STT;-------PANK    LD L,(IX+18)        LD H,(IX+19)        LD A,(IX+17):CP H:RET C        LD A,(IX+16):CP L:RET C        LD A,(IX+5):SUB 2,(IX+10)        LD B,0,C,A        OR A:SBC HL,BC:JR NC,$+5:LD HL,0        LD (IX+16),L        LD (IX+17),H        RET;---------------------------------------PANE    CALL PANELI        LD (IX+23),A,(IX+24),A        LD (IX+12),1        LD (IX+16),A,(IX+17),A,(IX+26),A        RETPANELI  ;i:IX - SOW        LD A,1        LD (STUS),A        XOR A        LD (IX+13),A        LD (IX+15),A        LD (IX+20),A        LD (IX+21),A        RETGETEN   ;i:IX - SOW;        o:DE - flag addres;          HL - ENTRY addres;          ENTRY(12), BRRR(8)        LD B,0,C,(IX+12):DEC C        LD L,(IX+16),H,(IX+17)        ADD HL,BCGETen   CALL GWE        PUSH HL        LD DE,BRRR,BC,8:LDIR        LD C,4:ADD HL,BC        LD DE,ENTRY,C,8:LDIR        INC HL:LD C,3:LDIR        XOR A:LD (DE),A;flg        POP HL        PUSH HL        LD C,11:ADD HL,BC:EX DE,HL        POP HL        LD A,(DE):OR A        RET;-------GETWE   LD L,(IX+16),H,(IX+17)GWE     CALL UM32        LD BC,DNT:ADD HL,BC        CALL CAT1        RETGwe     CALL UM32        LD BC,DNT+12:ADD HL,BC        CALL CAT1        LD A,(HL):OR A        RETUM32    LD B,0.5      SLA L:RL H,B.2      SLA B        LD A,H,C,#40        SUB C:JR C,GW:INC B        SUB C:JR C,GW:INC B        SUB C:JR C,GW:INC B        SUB CGW      ADD A,C:LD H,A        LD (IX+26),B        RET;-------CURPOZ  LD A,(IX+22):OR A:RET Z        CALL MNGC        LD DE,CURO        LD C,(IX+16),B,(IX+17)        LD L,(IX+23),H,(IX+24)        LD A,(IX+12)        DEC A:JR Z,LUPLup     LD A,(IX+12)        ADD HL,DE        LD (HL),C:INC HL        LD (HL),B:INC HL        LD (HL),A:INC HL        LD (HL),0:INC HL        LD (IX+12),1        LD (IX+16),0        LD (IX+17),0NEKO    OR A:SBC HL,DE        LD (IX+23),L,(IX+24),H        RET;-------LUP     LD A,B:OR C:JR NZ,Lup        LD A,L:OR H:JR Z,Lup        ADD HL,DE        DEC HL        DEC HL:LD A,(HL),(IX+12),A        DEC HL:LD A,(HL),(IX+17),A        DEC HL:LD A,(HL),(IX+16),A        JR NEKO;---------------------------------------ER0     LD HL,TER0        LD DE,#0116        LD BC,17ERX     CALL ERR0        PUSH IX        POP HL        CALL SXM        LD (IX+29),A,(IX+28),0        CALL DRACT        JR ORIGER1     LD HL,TER1        LD DE,#0116        LD BC,17        JR ERX;-------ORIGA2  LD A,(HEI):SUB 2:LD (IX+5),A        CALL PRWOWORIG    CALL IDE_INI        XOR A:CALL SEL_DEV:JR NZ,ER0        CALL HDD:JR NZ,ER1        LD HL,BRRR,B,4:CALL NOPING        JP PANEORIGA   CALL ORIGA2FOLD    CALL NEWDIR        CALL PANK        CALL WLIT,CR4eg        XOR A        RETFOLDER  CALL PANELI,CURPOZ        CALL FOLD        RET;---------------------------------------;GENERATING DIRECTORY IN RAM:NEWDIR  ;i:BRRR(4) - fstCLUS of NEW DIR        CALL PPATH        LD HL,DNT,(INTABL),HL        LD HL,BRRR:CALL GLSTCAT        LD HL,BRRR:CALL GIPAG        XOR A        LD (IX+18),A,(IX+19),A        LD (IX+26),A:CALL CAT1BUM     LD HL,LOBU        LD B,4:CALL LOAD512        PUSH AF        LD B,16:CALL NOPING        CALL DECODE        POP AF        CP #0F:JR Z,DOOM        LD A,H:CP LOBU[+8:JR Z,BUMDOOM    LD HL,(INTABL)        CALL A440        LD B,32:CALL NOPING        RETDECODE  ;i:LOBU(2048) - SEGMENT OF DIR;          INTABL(2) - ADDRESS        LD HL,LOBUNXFL    LD A,(HL):OR A:RET Z        CP ".":JP NZ,NEWEA        INC HL        LD A,(HL)        CP ".":JP NZ,RMFL1        DEC HLNEWEA   CP #E5:JP Z,RMFL        LD BC,11:ADD HL,BC        LD A,(HL):OR A:SBC HL,BC        BIT 3,A:JR NZ,RMFL        EX DE,HL        LD HL,(INTABL)        LD A,H:OR A:JR NZ,NPGE        LD A,(IX+26):CP 3:RET NC        INC (IX+26):CALL CAT1        LD HL,DNTNPGE    LD BC,12:ADD HL,BC        EX DE,HL        LD C,8:LDIR        LD A,".",(DE),A:INC DE        LD C,3:LDIR        LD A,(HL)        AND %00010000        XOR %00010000:JR NZ,TERN        LD C,4        EX DE,HL        OR A:SBC HL,BC        LD (HL)," "        ADD HL,BC        EX DE,HLTERN    EX DE,HL        LD C,13        OR A:SBC HL,BC        LD (HL),A;      Flag        LD C,3        SBC HL,BC        EX DE,HL        LD C,5:ADD HL,BC.2      LDI        LD C,8:ADD HL,BC        EX DE,HL        LD (HL),B;      Mark        LD C,10:SBC HL,BC        EX DE,HL.2      LDI        OR A:SBC HL,BC.2      LDI        ADD HL,BC.4      LDI        EX DE,HL        LD C,ELN-8:ADD HL,BC        LD (INTABL),HL        EX DE,HL        INC (IX+18):JP NZ,NXFL        INC (IX+19)        JP NXFL;-------RMFL1   DEC HLRMFL    LD C,32:ADD HL,BC        JP NXFL;---------------------------------------WLIT    XOR A        LD (IX+13),A        LD (IX+20),A        LD (IX+21),A        INC A        LD (STUS),A        CALL GETWE        LD DE,#0101        LD A,(IX+17):OR A:JR NZ,A123        LD A,(IX+16):OR A:JR NZ,A123        LD (IX+21),1A123    PUSH DE        CALL A440        LD BC,10:ADD HL,BC        LD A,(HL),(RSTB),A:INC HL        LD A,(HL),(RSTA),A:INC HL        LD A,(HL)        LD DE,TXTBU.12     LDI        LD DE,ELN-24:ADD HL,DE        POP DE        OR A:JR Z,EPG:INC (IX+13)        PUSH HL        LD HL,TXTBU        L