URBOPL        DW GEDPL;-------        DW SPKE;16        DW UPPP        DW DWWW        DW LFFF        DW RGGG        DW TABK        DW ENKE        DW ESC        DW BSPC        DW PGU        DW PGD        DW HOME        DW END;28;-------        DW F1        DW F2        DW F3        DW F4        DW F5        DW F6        DW F7        DW F8        DW F9        DW F10;-------        DW ALT;39        DW SHIFT        DW CTRL;-------        DW KBSCN;42;-------        DW FEX        DW CAPS;-------        DW ANYK;45        DW USPO;46        DW NUSP;-------        DW XLD512; 48        DW XSV512; 49        DW XGIPGPL;50        DW XTNTRY2;51        DW XCHTS;  52        DW XTNTRYN;53        DW XTMRKFL;54        DW XTMRKNX;55        DW FEX;    56        DW XSTREAM;57        DW XMKFL;  58 (OLD)        DW XFENTRY;59        DW XLD256; 60        DW XLDNON; 61        DW XGFILE; 62        DW XGDIR;  63;-------        DW MNGV_PL;64        DW MNGCVPL;65        DW GVmod;  66        DW GYoff;  67        DW GXoff;  68        DW GVtm;   69        DW GVtl;   70        DW GVsgp;  71;-------        DW XMKfile;72        DW XMKdir; 73.4      DW FEX;-------        DW MNG0_PL;78        DW MNG8_PL;79        DW MNG0VPL;80        DW MNG8VPL;81;-------GOPLG   LD A,PLHPG:CALL MNGC        LD DE,#C000+64-512PLG     LD HL,(PHED)        OR A:SBC HL,DE:JR C,PF        LD HL,512:ADD HL,DE:EX DE,HL        LD HL,ENTRY+8        PUSH DE:CALL CHext        POP DE:JR Z,PLG        EX DE,HL:LD BC,0-64:ADD HL,BC        LD DE,LOBU,BC,256:LDIR;-------        LD BC,(ARRR)        LD DE,(ARRR+2)        LD HL,(LOBU+161+2)        OR A:SBC HL,DE:JR C,PF,NZ,Pf        LD HL,(LOBU+161)        OR A:SBC HL,BC:JR C,PFPf      PUSH BC:CALL MYAU        POP HL        XOR A:CALL P8000        PUSH AF        LD A,2:CALL MNG8        CALL GIPAGPL        POP AF        CP 3:CALL Z,SEXY        EI:HALT        RET;-------PF      LD A,1        RETMYAU    LD A,(LOBU),(PPGP),A,HL,LOBU+35        ADD A,(HL)        ADD A,PLGPG        JP MNG8;-------RAJT    LD A,1AJT     PUSH AF:CALL MYAU        POP AF        CALL P8000        PUSH AF:LD A,2:CALL MNG8        POP AF        RET;-------RATM    LD A,PLHPG:CALL MNGC        LD HL,#C000+197,DE,(PHED),BC,512RAT     OR A:SBC HL,DE:JR NC,CAF        ADD HL,DE        LD A,(HL):ADD HL,BC        CP 2:JR NZ,RAT        LD BC,0-(512+197):ADD HL,BC        LD DE,LOBU,BC,256:LDIR        CALL AJTCAF     LD HL,0        JR MAF;-------TMAFK   CALL TAB4E:JR NZ,CAF        LD HL,(TABTM):INC HL        LD DE,(AFTM)        OR A:SBC HL,DE:JR NC,RATM        ADD HL,DEMAF     LD (TABTM),HL        RET;-------P8000   PUSH AF:XOR A:CALL MNGC        POP AF        LD BC,(PG0),(FEP),BC        PUSH BC:LD BC,(BRRR+2)        PUSH BC:LD BC,(BRRR)        PUSH BC        LD BC,(RSQBC),(RSQB2),BC        LD (IXBU),IX        CALL #8000        LD IX,(IXBU)        LD HL,(RSQB2),(RSQBC),HL        POP HL:LD (BRRR),HL        POP HL:LD (BRRR+2),HL        POP HL        PUSH AF:LD A,L:CALL MNG0,DEF        POP AF        RET;---------------------------------------        INCL "PS2P"        INCL "WCFX"        INCL "WCVLS"        INCL "WCVW"        INCL "WCINI";-------DEHR1   INCB "DEHR1M"MLZ4    INCB "MLZ40"GIG_PAL INCB "GIG_PAL"FONT    INCB "FONT32L"KOIWIN  INCB "KOI_WIN"NOre    ORG ENCODET,$        INCL "ENCODET";---------------------------------------        ORG #C000CORE    EQU #4000IDE_INI EQU CORESEL_DEV EQU CORE+3REINI   EQU CORE+6HDD     EQU CORE+9RDD     EQU CORE+12SDD     EQU CORE+15GIPAG   EQU CORE+18LOAD512 EQU CORE+21SAVE512 EQU CORE+24DOS_SWP EQU CORE+27GLSTCAT EQU CORE+30TLSTCAT EQU CORE+33SRHFCL  EQU CORE+36;EQU CORE+39MKSG    EQU CORE+42SVHDFL  EQU CORE+45RFRH    EQU CORE+48GENTRY  EQU CORE+51TENTRY  EQU CORE+54MKFILE  EQU CORE+57MKDIR   EQU CORE+60STAMP   EQU CORE+63ENCEN   EQU CORE+66DLSG    EQU CORE+69CHTOSE  EQU CORE+72SRHDR   EQU CORE+75SRHDRN  EQU CORE+78LOAD256 EQU CORE+81LOADNON EQU CORE+84;-------        INCB "WDOSFC29"