,HL        CALL NP6        JP OTNASS     LD B,24CSS     PUSH BC        CALL NP6        LD DE,SECBU,BC,#0200:LDIR        CALL NP8        EXX:LD HL,SECBU,BC,#0200:LDIR        EXX        POP BC        DJNZ CSS        RETTRY2    LD HL,LOBU,DE,TFMF;          TFC        CALL ZATA:JP NZ,TRY2A        LD A,2,(STA),A        PUSH HL        LD HL,(DADE),DE,LOBU        ADD HL,DE        POP DE        OR A:SBC HL,DE        EX DE,HL        LD A,D:CP #38:JP NC,NEmUZ        LD B,6        LD A,(HL):INC HL        CP "0":JP C,NEmUZ        LD A,(HL)        CP 50:JR Z,FFM        CP 60:JP NZ,NEmUZFFM     INC HL,HL        LD A,(HL):CP #38:JP NC,NEmUZ        DJNZ FFM        LD A,3,(STA),A        LD A,(TSFM):OR A:JP Z,NEmUZ        LD (TRIG),A        LD HL,(OTN+1)        DEC HL        LD DE,LOBU,BC,#4000-FMPL        LDIR        LD DE,FMDL        CALL GETMZ        LD HL,#C000+20,BC,FMLN        CALL GETPL        LD HL,FMDL+#22        LD A,(FMDL+8)        CP "1":JR Z,$+5:LD HL,FMDL+#32        LD DE,GTPR        LD B,32:CALL TFDFLL        LD B,32:CALL TFDFLL        LD A,5,(FMMZ),A        JP LUZZ+2TFDFLL  LD A,(HL):OR A:JR Z,$+3:INC HL        LD (DE),A:INC DE        DJNZ TFDFLL        INC HL        RETTRY2A   LD IX,LOBU;                  STC        LD A,(IX+#00):CP #CD:JR NZ,AST        LD A,(IX+#01):CP #52:JR NZ,AST        LD A,(IX+#02):CP #00:JR NZ,AST        LD DE,#06E9        JR STwPLAST     LD A,(IX+#00):CP #21:JR NZ,BST        LD A,(IX+#03):CP #C3:JR NZ,BST        LD A,(IX+#06):CP #C3:JR NZ,BST        LD A,(IX+#0A):CP #7E:JR NZ,BST        LD DE,#043CSTwPL   ADD IX,DEBST     LD A,(IX)        OR A:JP Z,TRY3        CP 24:JP NC,TRY3        LD A,(IX+6):CP #28:JP NC,TRY3        LD A,(IX+4):CP #20:JP NC,TRY3        LD A,(IX+2):CP #10:JP NC,TRY3        PUSH IX        POP HL        LD DE,7:ADD HL,DE        LD B,18SFT     LD A,(HL)        CP #18:INC HL:JR C,MRK        CP #20:JP C,TRY3        CP #7B:JP NC,TRY