   LD HL,PSCDL+25,DE,GTPR,C,20;B=0        LDIR        LD C,4        ADD HL,BC        LD DE,GTPR+32,C,20;B=0        LDIR        JP LUZZPSCFO   DB "PSC V1.0",0TRY5    LD HL,LOBU,DE,FTFO;           FT        CALL SRHMZ:JP NZ,TRY6        LD HL,LOBU+50        INC DE        CALL SRHMZ:JP NZ,TRY6        LD DE,FTDL        CALL GETMZ        LD HL,#C000+10,BC,FTLN        CALL GETPL        LD HL,FTDL+8,DE,GTPR,C,42;B=0        LDIR        JP LUZZFTFO    DB "Module: ",0        DB ";Fast Tracker v1.",0TRY6    LD HL,LOBU+10,DE,STPFO;      STP        CALL SRHMZ:JP NZ,TRY7aSUPA    LD A,(LOBU+9):OR A:JR Z,TRY8        LD DE,STPDL        CALL GETMZ        LD HL,#C000+12,BC,STPLN        CALL GETPL        LD HL,STPDL+38,DE,GTPR,C,25;B=0        LDIR        JP LUZZSTPFO   DB "KSA SOFTWARE COMPILATION OF"        DB 0TRY7a   LD HL,LOBU        LD A,(HL)        OR A:JR Z,TRY8        CP 24:JR NC,TRY8        LD B,4TBS     INC HL,HL        LD A,(HL)        OR A:JR Z,TRY8        CP #20:JR NC,TRY8        DJNZ TBS        JP SUPATRY8    LD HL,LOBU+1,DE,GTFO;         GT        CALL SRHMZ:JP NZ,TRY9        LD DE,GTDL        CALL GETMZ        LD HL,#C000+14,BC,GTLN        CALL GETPL        LD HL,GTDL+7,DE,GTPR,C,32;B=0        LDIR        JP LUZZGTFO    DB "GTR",0TRY9    LD HL,LOBU;                  ASC        LD A,(HL):OR A:JP Z,TRYA        DEC A:CP #40:JP NC,TRYA        INC HL        LD A,(HL):CP #60:JP NC,TRYA        INC HL        LD BC,#0320ASQ     INC HL        LD A,(HL):CP C:JP NC,TRYA        LD A,C:ADD A,#10:LD C,A        INC HL        DJNZ ASQ        LD A,(HL):CP #80:JP NC,TRYA        LD HL,(LOBU+2)        LD DE,LOBU:ADD HL,DE        LD C,(HL):INC HL        LD B,(HL):INC HL        LD E,(HL):INC HL        LD D,(HL)        EX DE,HL        OR A:SBC HL,BC:JP C,TRYA        LD DE,ASCDL        CALL GETMZ        LD HL,#C000+16,BC,ASCLN        CALL GETPL        LD HL,ASCDL,BC,128TRB     LD A,B:OR C:JR Z,TRABL        LD DE,ASCFO        LD A,(DE)        CPIR:JR NZ,TRABLSRCT    INC DE        LD A,(DE):OR A:JR Z,TRABL+3        CP (HL):JR NZ,TRB        INC HL        JR SRCTTRABL   LD HL,BELY        LD DE,GTPR,BC,20        LDIR        LD C,4        ADD HL,BC        LD DE,GTPR+32,C,20;B=0        LDIR        JP LUZZASCFO   DB "ASM COMPILATION OF ",0TRYA    LD HL,LOBU+1;                FLS        LD B,64PY      LD A,(HL):INC HL,HL        CP #60:JR C,PYC        DJNZ PYPYC     LD A,B:CP 64-4:JR NC,TRYB        LD A,(HL):CP #60:JR C,TRYB        LD HL,(LOBU)        LD DE,(LOBU+2)        SBC HL,DE:JR C,TRYB        LD DE,FLSDL        CALL GETMZ        LD HL,#C000+26,BC,FLSLN        CALL GETPL        JP LUZZTRYB    LD HL,LOBU,DE,FXFO;          FXM        CALL SRHMZ:JR NZ,TRYC        INC HL        LD A,(HL):CP #60:JR C,TRYCAYFXM   LD DE,FXDL        CALL GETMZ        LD HL,#C000+2,BC,FXLN        CALL GETPL2        JP LUZZFXFO    DB "FXSM",0TRYC    LD HL,LOBU;                  PT1        LD A,(HL):CP 24:JR NC,TRYD.3      INC HL        LD A,(HL):INC HL        OR (HL):JR NZ,TRYD        LD HL,LOBU+69,B,30-1BHG     LD A,(HL):INC HL        CP #20:JR C,TRYD        DJNZ BHGAYPT1   LD DE,PT1DL        CALL GETMZ        LD HL,#C000+4,BC,PT1LN        CALL GETPL2        LD HL,PT1DL+69,DE,GTPR,C,30:LDIR        JP LUZZTRYD    CALL NP6;                    SQT        CALL SQTIDEN:JP NZ,NEmUZ        LD HL,(DADE)        LD DE,(LOBU),(DAR+1),DE        SBC HL,DE:JR C,NTS        LD A,H:CP 3AYSQT   JR C,NTS        CALL NP8        LD HL,LOBU,DE,#E000,BC,#2000        PUSH HL,DE,BC        LDIR        CALL GSQT,NP8        POP BC,HL,DE        LDIR        CALL NP6DAR     LD HL,0,DE,LOBU        ADD HL,DE        LD BC,#4000-SQLN        LDIR        CALL SQTIDEN:JR NZ,NTS+3        LD HL,LOBU        EXX:LD DE,SQDL:EXX        CALL ASS        LD HL,NP8,(LAG+1),HL        LD HL,#C000+18,BC,SQLN        CALL GETPL        LD HL,NP1,(LAG+1),HL        CALL INSTM        LD A,1,(TSMZ),A,(TSFG),A        LD HL,#C064,(CTOL2+1),HL        JR NTS+3NTS     CALL GSQT,NP1        LD A,#64        JP LUZZ+2GSQT    LD DE,SQDL        CALL GETMZ        LD HL,#C000+18,BC,SQLN        JP GETPLSRHMZ   LD A,(DE):OR A:RET Z        CP (HL):RET NZ        INC HL,DE        JR SRHMZGETPL2  CALL NPe:JR $+2+3GETPL   CALL STN        LD A,(HL):INC HL        LD H,(HL),L,A        LD DE,LOBU        PUSH DE,BC        DI        CALL #C000;!        EILAG     CALL NP1        POP BC,HL        LD DE,#C000        LDIR        RETGETMZ   CALL NP1        LD HL,LOBU,BC,#2000        PUSH HL        LDIR        LD (DAHL),DE        CALL NP6        POP DE        LD HL,#C000,BC,#1C00        PUSH BC        LDIR        CALL NP1        POP BC        LD HL,LOBU,DE,(DAHL)        LDIR        RET;---------------------------------------NOSQT   LD A,1:OR A        RET;IDENTIFSQTIDEN LD IX,LOBU        LD A,(IX+#00):CP #3E:JR NZ,NOPLR        LD A,(IX+#02):CP #32:JR NZ,NOPLR        LD A,(IX+#05):CP #32:JR NZ,NOPLR        LD A,(IX+#08):CP #32:JR NZ,NOPLR        LD A,(IX+#0B):CP #01:JR NZ,NOPLR        LD HL,LOBU+SQLN        LD DE,LOBU        LD BC,#4000-SQLN        LDIRNOPLR   LD DE,(LOBU)        LD A,D:CP #40:JR NC,NOSQT        OR E:JR Z,NOSQT        LD HL,LOBU-1:ADD HL,DE        LD A,(HL):CP 9:JR NC,NOSQT        DEC HL        LD B,6SQ1     LD A,(HL):OR A:JR NZ,NOSQT        DEC HL        DJNZ SQ1        LD A,(HL)        OR A:JR Z,NOSQT        CP #21:JR NC,NOSQT        XOR A        RET;---------------------------------------ATIT    LD A,(TSFG):OR A:JR NZ,NEPL        LD BC,(MUZPL)        LD A,(GXMO):OR C:JR Z,NEPL        LD A,C:OR A:JR Z,NEPL        LD A,B:OR A:JR NZ,NEPL        LD HL,(TME):INC HL:LD (TME),HL        LD HL,(TMM):INC HL:LD (TMM),HL        CALL NP1CTOL    CALL PLAYM        LD A,(TSMZ):OR A:JR Z,NEPL        CP 1:JR NZ,NEPL        CALL NP8        LD BC,#FFFD,A,#FE:OUT ACTOL2   CALL PLAYM        LD BC,#FFFD,A,#FF:OUT ANEPL    RET;---------------------------------------LOAD512 LD A,48:JP WLDNPe     LD A,3:EXA:XOR A:JP WLDSTN     LD A,2:EXA:XOR A:JP WLDNP6     LD A,1:EXA:XOR A:JP WLD;BUFERNP1     LD A,4:EXA:XOR A:JP WLD;MODULE1NP8     LD A,5:EXA:XOR A:JP WLD;MODULE2;---------------------------------------TFMDET  XOR A:LD (DETKA),A        LD BC,#FFFD,A,%11111000:OUT A        EI:HALT        XOR A:OUT A        EI:HALT        LD B,#BF:OUT B        EI:HALT        LD B,#FF:INF        LD A,1:JP P,FM_EN        XOR AFM_EN   LD (TSFM),A;-------AYOFF   LD BC,#FFFD,A,#FE:OUT A        PUSH BC        CALL AYFF        POP BC        LD A,#FF:OUT AAYFF    LD BC,#FFFD        LD E,B,D,8        XOR A        OUT D        LD B,#BF:OUT A        LD B,E        INC D:OUT D        LD B,#BF:OUT A        LD B,E        INC D:OUT D        LD B,#BF:OUT A        RET;---------------------------------------MNWND   DB 0,0        DB 19,10;    X,Y        DB 42,10;    W,H        DB %01111111;PAPER+INK        DB 0        DB #0000;    BUFFER        DB 0,0;      LINES;---------------------------------------PTI     DB #21,#C6:DW PLIE;PT1        DB #21,#E1:DW PLI1;PT3        DB #21,#0C:DW PLI2;PT2        DB #18,#0A:DW PLI4;ASC        DB #21,#3C:DW PLI5;STC        DB #21,#68:DW PLI6;STP        DB #3E,#08:DW PLI7;SQT        DB #21,#AB:DW PLI8;PSC        DB #21,#65:DW PLI9;FTC        DB #21,#6E:DW PLI3;VTX        DB #21,#9A:DW PLIA;GTR        DB #21,#3D:DW PLIB;TFC        DB #21,#E6:DW PLIC;FLS        DB #C3,#09:DW PLID;FXMPTIX    DW PLIXNOWT    DW 0;-------PLI1    DB "Pro Tracker v3.x",0PLI2    DB "Pro Tracker 2",0PLI3    DB "Vortex Tracker II",0PLI4    DB "ASC Sound Master",0PLI5    DB "Sound Tracker",0PLI6    DB "Sound Tracker Pro",0PLI7    DB "SQ Tracker",0PLI8    DB "Pro Sound Creator v1.0x",0PLI9    DB "Fast Tracker v1.00",0PLIA    DB "Global Tracker",0PLIB    DB